import { useEffect, useRef } from "react";
import { useContext } from "react";
import { AppContext } from "../../AppContext/AppContext";
import { fetchBooks } from "../../api/FakeBookApi";
import Book from "./Book";
import MultiSelect from "./components/MultiSelect";
import Button from "@mui/material/Button";
import "./bookList.css";

const BookList = () => {
  const { books, setBooks, selectedBooks, clearFilter } =
    useContext(AppContext);
  const elementRef = useRef(null);

  useEffect(() => {
    const getAllBooks = async () => {
      const result = await fetchBooks();
      setBooks(result);
    };
    getAllBooks();
  }, [setBooks]);

  const handleClick = () => {
    clearFilter();
  };

  const addColor = () => {
    elementRef.current.style.backgroundColor = "black";
  };
  const removeColor = () => {
    elementRef.current.style.backGroundColor = "black";
  };

  return (
    <div>
      <MultiSelect />
      {selectedBooks && (
        <Button
          style={{ marginTop: 30 }}
          onClick={handleClick}
          variant="contained"
        >
          clear filter and show all books
        </Button>
      )}
      <div className="list">
        {selectedBooks &&
          selectedBooks.map((el) => (
            <ul key={el.id} ref={elementRef} onClick={addColor}>
              <Book
                key={el.id}
                title={el.title}
                author={el.author}
                publishingHouse={el.publishingHouse}
                pages={el.pages}
              />
            </ul>
          ))}
        {!selectedBooks &&
          books &&
          books.map((el) => (
            <ul key={el.id} ref={elementRef} onClick={addColor}>
              <Book
                key={el.id}
                title={el.title}
                author={el.author}
                publishingHouse={el.publishingHouse}
                pages={el.pages}
              />
            </ul>
          ))}
      </div>
    </div>
  );
};

export default BookList;
